import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

import axios from "axios";

import ImageList from "./components/ImageList";
import { Button } from "react-bootstrap";

class App extends Component {
  // default state object
  state = {
    images: []
  };

  componentDidMount() {
    axios
      .get("http://starlord.hackerearth.com/insta")
      .then(response => {
        // create an array of images only with relevant data
        const newImages = response.data.map(c => {
          return {
            image: c.Image,
            likes: c.likes,
            timestamp: c.timestamp
          };
        });

        // create a new "state" object without mutating
        // the original state object.
        const newState = Object.assign({}, this.state, {
          images: newImages
        });

        // store the new state object in the component's state
        this.setState(newState);
      })
      .catch(error => console.log(error));
  }

  sortByLike(images){
    // const newState = Object.assign({}, this.state, {
    //   images: newImages
    // });

    // store the new state object in the component's state

    const newState = [].concat(this.state.images)
    .sort((a, b) => a.likes > b.likes ? 1 : -1)
    this.setState(newState);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">React Insta Cloce App</h1>
        </header>
        <li>
          <Button onClick={this.sortByLike}>Sort</Button>
        </li>
        <ImageList images={this.state.images} />
      </div>
    );
  }
}

export default App;
